nlp = spacy.load('en_core_web_sm')


def sanitization_string(str_):

    bad_str_ = ""
    # 对responseData进行过滤操作
    if str_ == "":
        return bad_str_

    # 处理http/https协议的responseData
    if str_.startswith("HTTP/"):
        str_ = html_filter(str_)

    str_ = unicode_filter(str_)
    # 对所有response作统一过滤
    str_ = re.sub(r'<\s*p\s*>(.*?)<\s*/\s*p\s*>', ' ', str_)
    str_ = re.sub(r'\s+', ' ', str_)                # 多个空格匹配成为一个空格
    str_ = re.sub(r'-+', '-', str_)                 # 多个破折号匹配成为一个破折

    '''处理.符号问题 多. 边缘.等'''
    str_ = re.sub(r'\.+', '.', str_)
    str_ = re.sub(r'\s*\.\s', ' ', str_)
    str_ = re.sub(r'\s\.\s*', ' ', str_)
    str_ = re.sub(r'\s*\.$', ' ', str_)
    str_ = re.sub(r'^\.\s*', ' ', str_)

    str_ = ' '.join([char for char in str_.strip().split() if len(char) > 1])  # 匹配两侧空格字符串并将其删除
    str_ = str_.lower()
    str_ = filter_dictionary_string(str_)
    return str_


def tokenize(text):
    """
    使用nltk+spacy长英文字符串进行分词
    :param text:
    :return: list: [ word1, word2, word3, ..., ]
    """
    if hex_calc_proportion(text) < 0.5 and unicode_calc_proportion(text) < 0.2:
        text = sanitization_string(text)
        # print(text)
        words = word_tokenize(text)
        print(words)
        return words
    words = word_tokenize(text)
    # print(words)

    text = " ".join(words)
    doc = nlp(text)

    tokens = [token.text for token in doc]
    interpunctuations = [',', '.', ':', ';', '?', '(', ')', '[', ']', '&', '!', '*', '@', '#', '$', '%']   #定义标点符号列表
    tokens = [word for word in tokens if word not in interpunctuations]   #去除标点符号
    tokens_ = [word for word in tokens if len(word) > 1]

    print(tokens_)
    return tokens_


